// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

contract EnigmaticSlothCoin is ERC20, Ownable {
    uint256 private constant INITIAL_SUPPLY = 1e9 * 1e18; // 18 
    uint256 private constant BLOCKS_PER_DAY = 5760; // BSC 
    uint256 private constant AIRDROP_RATE_1000_AND_ABOVE = 2; // 0.002%
    uint256 private constant AIRDROP_RATE_BELOW_1000 = 1; // 0.0005%

    address public liquidityPool; // 

    struct StakingInfo {
        uint256 stakedAmount;
        uint256 lastClaimedBlock;
    }

    mapping(address => StakingInfo) public stakingInfo;

    event Staked(address indexed account, uint256 amount);
    event Unstaked(address indexed account, uint256 amount);
    event Claimed(address indexed account, uint256 amount);

    constructor(address initialOwner) ERC20("EnigmaticSlothCoin", "ESC") Ownable(initialOwner) {
        _mint(initialOwner, INITIAL_SUPPLY);
    }

    function _calculateAirdropAmount(address account) private view returns (uint256) {
        uint256 balance = balanceOf(account);
        if (balance >= 1000e18) {
            return (balance * AIRDROP_RATE_1000_AND_ABOVE) / 1e6; // 0.002%
        } else {
            return (balance * AIRDROP_RATE_BELOW_1000) / 1e6; // 0.0005%
        }
    }

    function stake(uint256 amount) external {
        require(amount > 0, "Amount must be greater than 0");
        require(balanceOf(msg.sender) >= amount, "Insufficient balance");

        if (stakingInfo[msg.sender].stakedAmount == 0) {
            stakingInfo[msg.sender].lastClaimedBlock = block.number;
        }

        stakingInfo[msg.sender].stakedAmount += amount;
        _transfer(msg.sender, address(this), amount);

        emit Staked(msg.sender, amount);
    }

    function unstake(uint256 amount) external {
        require(amount > 0, "Amount must be greater than 0");
        require(stakingInfo[msg.sender].stakedAmount >= amount, "Insufficient staked amount");

        stakingInfo[msg.sender].stakedAmount -= amount;
        _transfer(address(this), msg.sender, amount);

        emit Unstaked(msg.sender, amount);
    }

    function claimAirdrop() external {
        uint256 airdropAmount = _calculateAirdropAmount(msg.sender);
        require(airdropAmount > 0, "No airdrop available");

        uint256 blocksSinceLastClaim = block.number - stakingInfo[msg.sender].lastClaimedBlock;
        uint256 daysSinceLastClaim = blocksSinceLastClaim / BLOCKS_PER_DAY;
        require(daysSinceLastClaim > 0, "Airdrop already claimed today");

        stakingInfo[msg.sender].lastClaimedBlock = block.number;
        _mint(msg.sender, airdropAmount);

        emit Claimed(msg.sender, airdropAmount);
    }

    function setLiquidityPool(address _liquidityPool) external onlyOwner {
        liquidityPool = _liquidityPool;
    }

    function burn(uint256 amount) external onlyOwner {
        _burn(msg.sender, amount);
    }
}

contract LiquidityPool {
    IERC20 private enigmaticSlothCoin;

    constructor(address _enigmaticSlothCoin) {
        enigmaticSlothCoin = IERC20(_enigmaticSlothCoin);
    }

